const option = {
	//Tokens
	OPTION_Address: 'TSRvq7QsZKEQSEQVcYm45NSyjSCD5h8hPV',	

	async requestPriceUpdate(_pair_id){
		try {
			if (!tron.tronWeb) return null;
		  	const tokenContract = await tron.tronWeb.contract().at(token_contract_address);
		  	let res = await tokenContract.requestPriceUpdate(_pair_id).send({
		          shouldPollResponse: true,
		          callValue: 0
		      });
		  	console.log(res);
		  	return res;
		} catch (err) {
		  console.log(err);
		  return null;
		}
	},
}
export default option;
