///////////////////////////////////////////////////////////////
import React, { Component } from "react";
import "./Home.scss";
import TronLinkGuide from "../TronLinkGuide";
import utils from "../../utils";
import tron from "../../utils/tron";

export default class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
      USDT_balance:0,
      TRX_balance:0,
    };
    this.tick = this.tick.bind(this);
    this.intervalHandle = null;

  }
  async tick() { 
    console.log(tron.tronWeb,utils.address);
    this.setState({
      USDT_balance:await tron.getUSDTBalance(utils.address),
      TRX_balance: await tron.getTRXBalance(utils.address)
    },this.forceUpdate());

  }
  async componentWillUnmount(){
    //console.log("unmounted");
    clearInterval(this.intervalHandle);

  }
  async componentDidMount() {
    this.tick();
    this.intervalHandle = setInterval(this.tick, 1000);
    return;

  }
  render() {

    return (
      <div>

          <section className="site-section bg-light" id="contact-section">
            <div className="container">
              <div className="row mb-5">
                <div className="col-12 text-center">
                  <h3 className="section-sub-title">TEST OPTION</h3>

                </div>
              </div>
              <div className="row">
                <div className="col-md-6 mb-5">
                  <form action="#" className="p-5 bg-white">
                    <div className="row form-group">

                      <div className="col-md-12">
                        <label className="text-black" for="subject">Balance</label> <br/>
                        {this.state.USDT_balance} USDT <br/>
                        {this.state.TRX_balance} TRX
                      </div>
                      <br/>
                       <div className="col-md-12">

                          <a href={void(0)} className="btn btn-primary" onClick={()=>this.topup()}>Topup</a>


                      </div>

                    </div>

                    <p id="topup_message"></p>
                  </form>
                </div>
                <div className="col-md-6 mb-5">
                  <form action="#" className="p-5 bg-white">
                    <div className="row form-group">

                      <div className="col-md-12">
                        <label className="text-black" for="subject">Claim Your Reward</label> 
                          <input type="text" className="form-control" placeholder="Token Address" onChange={(e)=>this.updateClaimAddress(e)} />
                          <input type="number" className="form-control" placeholder="Your Reward" readOnly value={this.state.toClaimTokenAmount} />

                      </div>
                      <br/>
                       <div className="col-md-12">

                          <a href={void(0)} className="btn btn-primary" onClick={()=>this.claimTokenReward()}>Claim</a>


                      </div>

                    </div>

                    <p id="claimtoken_message"></p>
                  </form>
                </div>
              </div>

            </div>

        </section>          
      </div>

    );
  }
}
