///////////////////////////////////////////////////////////////
import React, { Component } from "react";
import "./Home.scss";
import TronLinkGuide from "../TronLinkGuide";
import TronWeb from "tronweb";
import Utils from "../../utils";

export default class Home extends Component {
  constructor(props) {
    super(props);
    this.state = {
    };


  }



  async componentDidMount() {


  }
  render() {

    return (
      <div>
          <TronLinkGuide tronwebReady={Utils.tronwebReady}/>
          <section className="site-section bg-light" id="contact-section">
            <div className="container">
              <div className="row mb-5">
                <div className="col-12 text-center">
                  <h3 className="section-sub-title">Staking Services</h3>
                  <h2 className="section-title mb-3">{contracts.stakingUser ? null : 'PLEASE CONNECT...'}</h2>
                  <p>Your balance:{this.state.balance} ETH</p>
                  <a href={void(0)} className="btn btn-primary" onClick={()=>this.Refresh()}>{this.state.connected && this.state.address != '' ? utils.shortaddr(this.state.address): 'Connect'}</a>
                  <h3>bfxLev Exchange Staking</h3>
                  <h4>bfx stakers earn 50% of ALL trading fees on the bfxLev exchange</h4>
                  <p>Total BFX staking: 
                  {contracts.stakingUser ? utils.toeth(contracts.stakingUser.totalStaking, 3) : 0} BFX</p>
                  <p>Wallet Balance: 
                  {contracts.stakingUser ? utils.toeth(contracts.stakingUser.bfxbalance, 3) : 0} BFX</p>
                  <p>Staking: 
                  {contracts.stakingUser ? utils.toeth(contracts.stakingUser.staking, 3) : 0} BFX</p>
                  <p>Your Unclaimed rewards: 
                  {contracts.stakingUser ? utils.toeth(contracts.stakingUser.due, 3) : 0} ETH</p>
                  <br/>
                  <a href={void(0)} className="btn btn-primary" onClick={()=>this.claim()}>CLAIM ETH</a>
                  <p id="claim_message"></p>
                </div>
              </div>

            </div>

        </section>          
      </div>

    );
  }
}
